import { useState } from "react";
import styles from "./PostingBoard.module.scss";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import { useSessionContext } from "../../../context/SessionContext";
import {} from "../../../lib/api";
export default function PostingBoard() {
  const { subjectid } = useParams();
  const { token } = useSessionContext();

  const location = useLocation();
  const category = location.pathname.split("/")[2];
  const [titleInput, setTitleInput] = useState("");
  const [contentInput, setContentInput] = useState("");

  const navigate = useNavigate();

  // titleInput 상자 관리
  const handleTitleInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    setTitleInput(e.target.value);
  };

  // contentInput 상자 관리
  const handleContentInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    setContentInput(e.target.value);
  };

  // 게시글 올리기
  const postReply = (
    token: string | null,
    post_id: number,
    content: string
  ) => {
    apiPostReply(token, post_id, content)
      .then((res) => {
        getPost(token, postId, category);
        setReply("");
      })
      .catch((err) => console.log(err));
  };
  const goBack = () => {
    navigate(-1);
  };

  return (
    <div className={styles.container}>
      <form>
        <header>
          <h2>게시글 작성</h2>
          <div className={styles.buttonContainer}>
            <button className={styles.cancel} onClick={goBack}>
              취소
            </button>
            <input type='submit' className={styles.submit} value='등록' />
          </div>
        </header>
        <section>
          <div className={styles.titleContainer}>
            <span className={styles.title}>제목</span>
            <span className={styles.content}>내용</span>
          </div>
          <div className={styles.inputContainer}>
            <input
              placeholder='제목 입력'
              value={titleInput}
              onChange={handleTitleInput}
              className={styles.titleInput}
              // onChange={onChangeTitle}
            ></input>
            <input
              placeholder='내용 입력'
              value={contentInput}
              onChange={handleContentInput}
              className={styles.contentInput}
              // onChange={onChangeTitle}
            ></input>
          </div>
        </section>
      </form>
    </div>
  );
}
