import { createContext, useContext, useState, useEffect } from "react";
import { User } from "../lib/types";
import { apiLogin, apiLogout } from "../lib/api";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

type SessionContextType = {
  isLoggedIn: boolean;
  user: User | null;
  token: string | null;
  login: (username: string, password: string) => Promise<any>;
  logout: (token: string) => Promise<any>;
};

const SessionContext = createContext<SessionContextType>(
  {} as SessionContextType
);

export function SessionProvider({ children }: { children: React.ReactNode }) {
  const [isLoggedIn, setIsLogggedIn] = useState(false);
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(null);

  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const res = await getRefreshToken(); //렌더링 시 refreshToken 요청, 우선 false return하게 임의로 구현해둠
        // if (res !== undefined) {
        //   const owner = await getLoggedInUser(res);
        //   setAccount(owner);
        //   setIsLoggedIn(true);
        // } else {
        //   setIsLoggedIn(false);
        // }
        if (!res) {
          toast("로그인 후 이용해주세요");
          navigate("/login");
        }
      } catch (e) {
        setIsLogggedIn(false);
        console.error(e);
      }
    })();
  }, []);

  const getRefreshToken = () => {
    return false;
  };

  const login = async (email: string, password: string): Promise<any> => {
    try {
      const res = await apiLogin(email, password);
      setUser(res.data);
      setToken(res.data.token);
      navigate("/");
    } catch (err) {
      console.log(err);
      toast("이메일 또는 비밀번호가 틀렸습니다.", {
        position: "top-center",
        theme: "colored",
      });
    }
  };

  const logout = async (token: string) => {
    try {
      const res = await apiLogout(token);
      setUser(null);
      setToken(null);
      navigate("/login/");
    } catch (err) {
      return console.log(err);
    }
  };

  return (
    <SessionContext.Provider
      value={{
        isLoggedIn,
        user,
        token,
        login,
        logout,
      }}
    >
      {children}
      <ToastContainer />
    </SessionContext.Provider>
  );
}

export function useSessionContext() {
  return useContext(SessionContext);
}
