import styles from "./ModuleBlock.module.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCaretDown,
  faCaretRight,
  faPaperclip,
} from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";

export interface ModuleInterface {
  name: string;
  contents: string[];
}

const Module = ({ module }: { module: ModuleInterface }) => {
  const [moduleModal, setModuleModal] = useState(true);

  const handleModuleModal = () => {
    if (moduleModal) {
      setModuleModal(false);
    } else {
      setModuleModal(true);
    }
  };

  const closeModuleModal = () => {
    // setAniState(true);
    // setTimeout(() => {
    // setAniState(false);
    setModuleModal(false);
    // }, 500);
    // setIsSelected(0);
  };

  return (
    <div className={styles.moduleContainer}>
      <header onClick={openModuleModal}>
        <FontAwesomeIcon icon={faCaretDown} className={styles.caretDown} />
        {module.name}
      </header>

      {module.contents.map((content) => (
        <article>
          <FontAwesomeIcon icon={faPaperclip} className={styles.paperClip} />
          <span>{content}</span>
        </article>
      ))}
    </div>
  );
};

export default function ModuleBlock() {
  const modules = [
    { name: "1주차", contents: ["Lecture note 1", "Lecture note 1"] },
    { name: "2주차", contents: ["Lecture note 3", "Lecture note 4"] },
  ];
  return (
    <div className={styles.wrapper}>
      {modules.map((module) => (
        <Module module={module} />
      ))}
    </div>
  );
}
