import SubjectTemplate from "../SubjectTemplate";
import styles from "./StudentsPage.module.scss";
import { useParams } from "react-router-dom";
import { useState } from "react";
import Searchbar from "../Searchbar";

export default function StudentsPage() {
  const { subjectname } = useParams();

  const [searchValue, setSearchValue] = useState<string>("");
  const [searchType, setSearchType] = useState<string>("");

  return (
    <SubjectTemplate subject={subjectname as string} page='수강생'>
      <section>
        <Searchbar searchValue={searchValue} setSearchValue={setSearchValue} />
        <select
          className={styles.typeSearchbar}
          value={searchType}
          onChange={(e: React.ChangeEvent<HTMLSelectElement>) => {
            e.preventDefault();
            setSearchType(e.target.value);
          }}
        >
          <option value='any'>모든 역할</option>
          <option value='student'>학생</option>
          <option value='professor'>교수</option>
        </select>
      </section>

      <table className={styles.table}>
        <thead>
          <tr>
            <th className={styles.name}>이름</th>
            <th className={styles.subject}>과목</th>
            <th className={styles.type}>역할</th>
          </tr>
        </thead>
        <tbody>
          <tr
            className={styles.row}
            key={menu.id}
            onClick={(e) => {
              e.preventDefault();
              showDetail();
              setSelectedMenu(menu.id);
            }}
          >
            <td className={styles.id}>{menu.id}</td>
            <td id={menu.name} className={styles.name}>
              {" "}
              {menu.name}
            </td>
            <td className={styles.type}>{kor_menutype(menu.type)}</td>
            <td className={styles.price}>{menu.price}</td>
            <td className={styles.rating}></td>
          </tr>
        </tbody>
      </table>
    </SubjectTemplate>
  );
}
