import styles from "./ModuleBlock.module.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCaretDown,
  faCaretRight,
  faPaperclip,
} from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";

export interface ModuleInterface {
  name: string;
  contents: string[];
}

const Module = ({ module }: { module: ModuleInterface }) => {
  const [toggleOpen, setToggleOpen] = useState<boolean>(true);

  return (
    <div className={styles.moduleContainer}>
      <header onClick={() => setToggleOpen(!toggleOpen)}>
        <FontAwesomeIcon
          icon={toggleOpen ? faCaretDown : faCaretRight}
          className={styles.caret}
        />
        {module.name}
      </header>

      {toggleOpen &&
        module.contents.map((content) => (
          <article>
            <FontAwesomeIcon icon={faPaperclip} className={styles.paperClip} />
            <span>{content}</span>
          </article>
        ))}
    </div>
  );
};

export default function ModuleBlock() {
  const modules = [
    { name: "1주차", contents: ["Lecture note 1", "Lecture note 1"] },
    { name: "2주차", contents: ["Lecture note 3", "Lecture note 4"] },
  ];
  return (
    <div className={styles.wrapper}>
      <button onClick={handleEveryToggleOpen}>모든 모듈 접기</button>
      {modules.map((module) => (
        <Module module={module} />
      ))}
    </div>
  );
}
