import {
  createContext,
  useContext,
  useState,
  PropsWithChildren,
  useCallback,
} from "react";
import { User } from "../lib/types";
import { apiLogin } from "../lib/api";

type SessionContextType = {
  isLoggedIn: boolean;
  user: User | null;
  login: (email: string, password: string) => void;
  handleGoogleToken: (input: string | undefined) => void;
};

const SessionContext = createContext<SessionContextType>(
  {} as SessionContextType
);

export function SessionProvider({ children }: { children: React.ReactNode }) {
  const [isLoggedIn, setIsLogggedIn] = useState(false);
  const [googleToken, setGoogleToken] = useState<string | undefined>("");
  const [user, setUser] = useState<User | null>(null);

  const handleGoogleToken = (input: string | undefined) => {
    setGoogleToken(input);
  };

  const login = useCallback(async (email: string, password: string) => {
    return apiLogin(email, password)
      .then((res) => {
        setUser(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  // const login = useCallback(async (username: string, password: string) => {
  //   loginCancelSource.current?.cancel();
  //   setResult((prev) => ({ ...prev, loading: true }));
  //   loginCancelSource.current = axios.CancelToken.source();
  //   return apiLogin(username, password, loginCancelSource.current.token)
  //     .then((res) => {
  //       setResult({ loginInfo: res.data, loading: false });
  //       return { payload: res.data.owner };
  //     })
  //     .catch((err) => {
  //       if (axios.isCancel(err)) return { canceled: true } as const;
  //       throw err;
  //     })
  //     .finally(() => {
  //       loginCancelSource.current = null;
  //     });
  // }, []);

  return (
    <SessionContext.Provider
      value={{ isLoggedIn, user, login, handleGoogleToken }}
    >
      {children}
    </SessionContext.Provider>
  );
}

export function useSessionContext() {
  return useContext(SessionContext);
}
