export function boardIdentifier(category: string) {
  if (category === "announcements") {
    return "공지";
  } else {
    return "Q&A";
  }
}

export function timestampToDate(timestamp: number, dateOrTime: string) {
  const modifiedTimestamp = timestamp * 1000;
  const date = new Date(modifiedTimestamp);
  if (dateOrTime === "date") {
    return (
      date.getFullYear() + "-" + (date.getMonth() + 1) + "-" + date.getDate()
    );
  } else {
    return date.getHours() + ":" + date.getMinutes();
  }
}
