import { useState } from "react";
import styles from "./PostingBoard.module.scss";
import { useNavigate, useLocation, useParams } from "react-router-dom";
import { useSessionContext } from "../../../context/SessionContext";
import { apiPostNewPost } from "../../../lib/api";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function PostingBoard() {
  const { subjectid } = useParams();
  const { token } = useSessionContext();
  const location = useLocation();
  const category = location.pathname.split("/")[2];
  const [titleInput, setTitleInput] = useState("");
  const [contentInput, setContentInput] = useState("");
  const navigate = useNavigate();

  // titleInput 상자 관리
  const handleTitleInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    setTitleInput(e.target.value);
  };

  // contentInput 상자 관리
  const handleContentInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    setContentInput(e.target.value);
  };

  // 게시글 올리기
  const postNewPost = (
    token: string | null,
    class_id: number,
    title: string,
    content: string,
    category: string
  ) => {
    apiPostNewPost(token, class_id, title, content, category)
      .then((res) => {
        toast("게시글을 성공적으로 등록했습니다.", {
          position: "top-center",
          theme: "colored",
          autoClose: 1000,
        });
        setTitleInput("");
        setContentInput("");
        navigate(-1);
      })
      .catch((err) => console.log(err));
  };

  const goBack = () => {
    navigate(-1);
  };

  return (
    <div className={styles.container}>
      <form>
        <header>
          <h2>게시글 작성</h2>
          <div className={styles.buttonContainer}>
            <button className={styles.cancel} onClick={goBack}>
              취소
            </button>
            <input
              type='submit'
              className={styles.submit}
              value='등록'
              onClick={(e) => {
                e.preventDefault();
                postNewPost(
                  token,
                  Number(subjectid),
                  titleInput,
                  contentInput,
                  category
                );
              }}
            />
          </div>
        </header>
        <section>
          <div className={styles.titleContainer}>
            <span className={styles.title}>제목</span>
            <span className={styles.content}>내용</span>
          </div>
          <div className={styles.inputContainer}>
            <input
              placeholder='제목 입력'
              value={titleInput}
              onChange={handleTitleInput}
              className={styles.titleInput}
            ></input>
            <input
              placeholder='내용 입력'
              value={contentInput}
              onChange={handleContentInput}
              className={styles.contentInput}
            ></input>
          </div>
        </section>
      </form>
      <ToastContainer />
    </div>
  );
}
