import styles from "./ModuleBlock.module.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCaretDown,
  faCaretRight,
  faPaperclip,
} from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";

export interface ModuleInterface {
  name: string;
  contents: string[];
}

const Module = ({
  module,
  openedToggles,
}: {
  module: ModuleInterface;
  openedToggles: number[];
}) => {
  const [toggleOpen, setToggleOpen] = useState<boolean>(true);

  return (
    <div className={styles.moduleContainer}>
      <header onClick={() => setToggleOpen(!toggleOpen)}>
        <FontAwesomeIcon
          icon={toggleOpen ? faCaretDown : faCaretRight}
          className={styles.caret}
        />
        {module.name}
      </header>

      {toggleOpen &&
        module.contents.map((content) => (
          <section>
            <FontAwesomeIcon icon={faPaperclip} className={styles.paperClip} />
            <span>{content}</span>
          </section>
        ))}
    </div>
  );
};

export default function ModuleBlock() {
  const modules = [
    { id: 0, name: "1주차", contents: ["Lecture note 1", "Lecture note 2"] },
    { id: 1, name: "2주차", contents: ["Lecture note 3", "Lecture note 4"] },
  ];

  // module의 개수와 같은 개수의 1로 이루어진 배열 생성 (처음에는 모든 모듈이 열려 있는 상태)
  // 모듈(토글)이 열려 있으면 1, 아니면 0
  const [openedToggles, setOpenedToggles] = useState<number[]>([
    Array(modules.length).fill(1)[0],
  ]);

  const handleSingleToggle = (id: number) => {
    if (openedToggles[id]) {
      const newOpenedToggles = openedToggles.splice(id, 1, 0);
      setOpenedToggles(newOpenedToggles);
    } else {
      const newOpenedToggles = openedToggles.splice(id, 1, 1);
      setOpenedToggles(newOpenedToggles);
    }
  };

  // 체크박스 전체 선택
  const handleAllToggle = () => {
    if (1 in openedToggles) {
      const newOpenedToggles = Array(modules.length).fill(0)[0];
      setOpenedToggles(newOpenedToggles);
    } else {
      const newOpenedToggles = Array(modules.length).fill(1)[0];
      setOpenedToggles(newOpenedToggles);
    }
  };

  return (
    <div className={styles.wrapper}>
      <div className={styles.headerbar}>
        <button onClick={handleAllToggle}>모든 모듈 접기</button>
      </div>
      <article>
        {modules.map((module) => (
          <Module module={module} openedToggles={openedToggles} />
        ))}
      </article>
    </div>
  );
}
