import { createContext, useContext, useState } from "react";
import { User } from "../lib/types";
import { apiLogin, apiLogout } from "../lib/api";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

type SessionContextType = {
  isLoggedIn: boolean;
  user: User | null;
  token: string | null;
  login: (username: string, password: string) => Promise<any>;
  logout: (token: string) => Promise<any>;
  // handleGoogleToken: (input: string | undefined) => void;
};

const SessionContext = createContext<SessionContextType>(
  {} as SessionContextType
);

export function SessionProvider({ children }: { children: React.ReactNode }) {
  const [isLoggedIn, setIsLogggedIn] = useState(false);
  const [googleToken, setGoogleToken] = useState<string | undefined>("");
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(null);

  // const handleGoogleToken = (input: String | undefined) => {
  //   setGoogleToken(input);
  // };

  const navigate = useNavigate();
  const login = (email: string, password: string): Promise<any> => {
    return apiLogin(email, password)
      .then((res) => {
        setUser(res.data);
        setToken(res.data.token);
        navigate("/");
      })
      .catch((err) => {
        console.log(err);
        toast("이메일 또는 비밀번호가 틀렸습니다.", {
          position: "top-center",
          theme: "colored",
        });
      });
  };

  const logout = (token: string) => {
    return apiLogout(token)
      .then((res) => {
        setUser(null);
        setToken(null);
        navigate("/login/");
      })
      .catch((err) => console.log(err));
  };

  return (
    <SessionContext.Provider
      value={{
        isLoggedIn,
        user,
        token,
        login,
        logout,
        //  handleGoogleToken
      }}
    >
      {children}
      <ToastContainer />
    </SessionContext.Provider>
  );
}

export function useSessionContext() {
  return useContext(SessionContext);
}
