import { Dispatch, SetStateAction } from "react";
import styles from "./Searchbar.modules.scss";

interface SearchbarPropsType {
  searchValue: string;
  setSearchValue: Dispatch<SetStateAction<string>>;
}

function Searchbar({ searchValue, setSearchValue }: SearchbarPropsType) {
  let timer: any = null;

  function throttling(event: React.ChangeEvent<HTMLInputElement>) {
    if (!timer) {
      timer = setTimeout(() => {
        timer = null;
        setSearchValue(event.target.value);
        console.log(searchValue);
      }, 500);
    }
  }

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    throttling(event);
  };
  return (
    <div className={styles.menuNameSearchContainer}>
      <p>
        이름:{" "}
        <input
          className={styles.menuNameSearchbar}
          placeholder='검색어 입력'
          onChange={handleInputChange}
        />
      </p>
    </div>
  );
}

export default Searchbar;
