import styles from "./ImageModal.module.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faImage,
  faXmark,
  faArrowDown,
} from "@fortawesome/free-solid-svg-icons";
import Modal from "react-modal";
import React, {
  useState,
  useCallback,
  useRef,
  useEffect,
  ChangeEvent,
  FormEvent,
} from "react";
import { toast } from "react-toastify";
import { apiUploadImage } from "../../../lib/api";
import { useSessionContext } from "../../../context/SessionContext";

type ImageModalType = {
  isModalOpen: boolean;
  toggleModal: () => void;
  imageFile: File | null;
  setImageFile: React.Dispatch<File | null>;
};

export function ImageModal({
  isModalOpen,
  toggleModal,
  imageFile,
  setImageFile,
}: ImageModalType) {
  const { token } = useSessionContext();
  const [isDragging, setIsDragging] = useState<boolean>(false);
  const dragRef = useRef<HTMLLabelElement | null>(null);

  const onChangeFiles = useCallback(
    (e: ChangeEvent<HTMLInputElement> | any): void => {
      let selectFiles: File[];
      if (e.type === "drop") {
        selectFiles = e.dataTransfer.files;
      } else {
        selectFiles = e.target.files;
      }
      setImageFile(selectFiles[0]);
    },
    [imageFile]
  );

  const onSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (imageFile) {
      let formData = new FormData();
      formData.append("file", imageFile);
      apiUploadImage(token, formData)
        .then(() => {
          console.log("이미지 파일명: ", imageFile.name);
          console.log("FormData: ", imageFile);
          toast("등록되었습니다!", {
            position: "top-center",
            theme: "colored",
          });
          toggleModal();
        })
        .catch((r) => console.log(r));
    }
  };

  const handleDragIn = useCallback((e: DragEvent): void => {
    e.preventDefault();
    e.stopPropagation();
  }, []);

  const handleDragOut = useCallback((e: DragEvent): void => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  }, []);

  const handleDragOver = useCallback((e: DragEvent): void => {
    e.preventDefault();
    e.stopPropagation();
    if (e.dataTransfer!.files) {
      setIsDragging(true);
    }
  }, []);

  const handleDrop = useCallback(
    (e: DragEvent): void => {
      e.preventDefault();
      e.stopPropagation();
      onChangeFiles(e);
      setIsDragging(false);
    },
    [onChangeFiles]
  );

  const initDragEvents = useCallback((): void => {
    if (dragRef.current !== null) {
      dragRef.current.addEventListener("dragenter", handleDragIn);
      dragRef.current.addEventListener("dragleave", handleDragOut);
      dragRef.current.addEventListener("dragover", handleDragOver);
      dragRef.current.addEventListener("drop", handleDrop);
    }
  }, [handleDragIn, handleDragOut, handleDragOver, handleDrop]);

  const resetDragEvents = useCallback((): void => {
    if (dragRef.current !== null) {
      dragRef.current.removeEventListener("dragenter", handleDragIn);
      dragRef.current.removeEventListener("dragleave", handleDragOut);
      dragRef.current.removeEventListener("dragover", handleDragOver);
      dragRef.current.removeEventListener("drop", handleDrop);
    }
  }, [handleDragIn, handleDragOut, handleDragOver, handleDrop]);

  useEffect(() => {
    initDragEvents();
    return () => resetDragEvents();
  }, [initDragEvents, resetDragEvents]);

  return (
    <Modal
      isOpen={isModalOpen}
      onRequestClose={toggleModal}
      className={styles.modal}
    >
      <header>
        <p className={styles.title}>프로필 사진 변경</p>
        <FontAwesomeIcon
          icon={faXmark}
          className={styles.x}
          onClick={toggleModal}
        />
      </header>
      <div className={styles.dragdrop}>
        <input
          type='file'
          id='fileUpload'
          style={{ display: "none" }}
          accept='image/*'
          onChange={onChangeFiles}
        />
        <label
          className={
            isDragging ? `${styles.file} ${styles.dragging}` : styles.file
          }
          htmlFor='fileUpload'
          ref={dragRef}
        >
          {imageFile ? (
            <img
              className={styles.image}
              src={URL.createObjectURL(imageFile)}
              alt='사진'
            />
          ) : (
            <FontAwesomeIcon
              icon={isDragging ? faArrowDown : faImage}
              className={styles.fa}
            />
          )}
          <p>
            여기에 파일 끌어서 놓기
            <br />
            혹은
          </p>
          <label className={styles.button} htmlFor='fileUpload'>
            파일 선택
          </label>
          <br />
          <div className={styles.fileName}>
            {imageFile ? imageFile.name : ""}
          </div>
        </label>
      </div>
      <form name='photo' encType='multipart/form-data' onSubmit={onSubmit}>
        <button className={styles.cancel} onClick={toggleModal}>
          취소
        </button>
        <input
          type={imageFile ? "submit" : "button"}
          style={{ display: "none" }}
          id='submitImage'
          onClick={
            !imageFile
              ? () =>
                  toast("사진을 선택해 주세요!", {
                    position: "top-center",
                    theme: "colored",
                  })
              : undefined
          }
        />
        <label className={styles.save} htmlFor='submitImage'>
          저장
        </label>
      </form>
    </Modal>
  );
}
